import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Productlist.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products from the API
    axios.get('http://localhost:5000/api/products')
      .then((response) => {
        console.log('Products fetched:', response.data); // Log data for verification
        setProducts(response.data); // Set the products state
      })
      .catch((error) => {
        console.error('There was an error fetching the products!', error);
        alert('Failed to fetch products. Please try again later.');
      });
  }, []); // Empty dependency array to run only once

  return (
    <div className="product-list">
      <h2 className="product-list-title">Available Rental Properties</h2>
      {products.length === 0 ? ( // Conditional rendering if no products
        <p>No products available at the moment.</p>
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product._id} className="product-card">
              <img
                src={product.imageUrl}
                alt={product.name}
                className="product-image"
              />
              <div className="product-details">
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p>${product.price}</p>
                <button className="add-to-cart">Add to Cart</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ProductList;
