import React, { useState } from "react";
import './Login.css';



const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = (event) => {
    event.preventDefault();
    const usernameError = username.trim() === '' ? 'Username is required' : '';
    const passwordError = password.trim() === '' ? 'Password is required' : '';

    if (usernameError || passwordError) {
      setErrors({ usernameError, passwordError });
      return;
    }

    // Call API or perform login logic here
    console.log('Login successful!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Login</h2>
      <label>
        Username:
        <input
          type="text"
          value={username}
          onChange={(event) => setUsername(event.target.value)}
          placeholder="Enter your username"
        />
        {errors.usernameError && <div style={{ color: 'red' }}>{errors.usernameError}</div>}
      </label>
      <br />
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
          placeholder="Enter your password"
        />
        {errors.passwordError && <div style={{ color: 'red' }}>{errors.passwordError}</div>}
      </label>
      <br />
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginForm;
