import React from "react";



function About() {
  return (
    <div className="about-container">
      <img src="https://i0.wp.com/mediachomp.com/wp-content/uploads/2021/02/il_1140xN.2493203095_fpyy.jpg?resize=650%2C903&ssl=1" alt="About Image" className="about-image" />
      <div className="about-content">
        <h1>About Us</h1>
        <p>
          Welcome to our handmade and craft marketplace! We are dedicated to providing you with
          unique, handcrafted products made with love and care by artisans from around the world.
          Our mission is to connect creators with customers who appreciate the art of handmade
          craftsmanship.
        </p>
      </div>
    </div>
  );
}

export default About;
